// src/components/auth/Profile.js
import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import './Auth.css';

const Profile = () => {
  const { currentUser, updateProfile, changePassword, logout } = useContext(AuthContext);
  
  // Profile update state
  const [name, setName] = useState(currentUser?.name || '');
  const [email, setEmail] = useState(currentUser?.email || '');
  const [profileMessage, setProfileMessage] = useState('');
  const [profileError, setProfileError] = useState('');
  
  // Password change state
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [passwordMessage, setPasswordMessage] = useState('');
  const [passwordError, setPasswordError] = useState('');
  
  const handleProfileUpdate = (e) => {
    e.preventDefault();
    setProfileMessage('');
    setProfileError('');
    
    if (!name || !email) {
      setProfileError('Name and email are required');
      return;
    }
    
    const success = updateProfile({ name, email });
    
    if (success) {
      setProfileMessage('Profile updated successfully');
    } else {
      setProfileError('Failed to update profile');
    }
  };
  
  const handlePasswordChange = (e) => {
    e.preventDefault();
    setPasswordMessage('');
    setPasswordError('');
    
    if (!oldPassword || !newPassword || !confirmNewPassword) {
      setPasswordError('All password fields are required');
      return;
    }
    
    if (newPassword !== confirmNewPassword) {
      setPasswordError('New passwords do not match');
      return;
    }
    
    if (newPassword.length < 6) {
      setPasswordError('Password must be at least 6 characters');
      return;
    }
    
    const success = changePassword(oldPassword, newPassword);
    
    if (success) {
      setPasswordMessage('Password changed successfully');
      setOldPassword('');
      setNewPassword('');
      setConfirmNewPassword('');
    } else {
      setPasswordError('Current password is incorrect');
    }
  };
  
  return (
    <div className="profile-container">
      <h1>Account Settings</h1>
      
      <div className="profile-card">
        <h2>Profile Information</h2>
        {profileMessage && <div className="auth-success">{profileMessage}</div>}
        {profileError && <div className="auth-error">{profileError}</div>}
        
        <form className="auth-form" onSubmit={handleProfileUpdate}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <button type="submit" className="auth-button">Update Profile</button>
        </form>
      </div>
      
      <div className="profile-card">
        <h2>Change Password</h2>
        {passwordMessage && <div className="auth-success">{passwordMessage}</div>}
        {passwordError && <div className="auth-error">{passwordError}</div>}
        
        <form className="auth-form" onSubmit={handlePasswordChange}>
          <div className="form-group">
            <label htmlFor="oldPassword">Current Password</label>
            <input
              type="password"
              id="oldPassword"
              value={oldPassword}
              onChange={(e) => setOldPassword(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="newPassword">New Password</label>
            <input
              type="password"
              id="newPassword"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="confirmNewPassword">Confirm New Password</label>
            <input
              type="password"
              id="confirmNewPassword"
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              required
            />
          </div>
          
          <button type="submit" className="auth-button">Change Password</button>
        </form>
      </div>
      
      <div className="profile-card danger-zone">
        <h2>Account Actions</h2>
        <button onClick={logout} className="logout-button">Log Out</button>
      </div>
    </div>
  );
};

export default Profile;
