// src/components/settings/CategoryManagement.js
import React, { useState, useContext, useEffect } from 'react';
import { ExpenseContext } from '../../context/ExpenseContext';
import { AuthContext } from '../../context/AuthContext';
import './CategoryManagement.css';

const CategoryManagement = () => {
  const { currentUser } = useContext(AuthContext);
  const { expenses } = useContext(ExpenseContext);
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  // Load categories from localStorage
  useEffect(() => {
    if (currentUser) {
      const savedCategories = JSON.parse(
        localStorage.getItem(`categories_${currentUser.id}`)
      ) || [
        'Food', 'Housing', 'Transportation', 'Entertainment', 
        'Utilities', 'Healthcare', 'Shopping', 'Personal Care', 'Education', 'Other'
      ];
      
      setCategories(savedCategories);
    }
  }, [currentUser]);
  
  // Save categories to localStorage whenever they change
  useEffect(() => {
    if (currentUser && categories.length > 0) {
      localStorage.setItem(
        `categories_${currentUser.id}`, 
        JSON.stringify(categories)
      );
    }
  }, [categories, currentUser]);
  
  const handleAddCategory = () => {
    setError('');
    setSuccess('');
    
    // Validation
    if (!newCategory.trim()) {
      setError('Category name cannot be empty');
      return;
    }
    
    if (categories.includes(newCategory.trim())) {
      setError('This category already exists');
      return;
    }
    
    // Add the new category
    setCategories([...categories, newCategory.trim()]);
    setNewCategory('');
    setSuccess('Category added successfully');
    
    // Clear success message after 3 seconds
    setTimeout(() => {
      setSuccess('');
    }, 3000);
  };
  
  const handleDeleteCategory = (categoryToDelete) => {
    setError('');
    setSuccess('');
    
    // Check if category is in use
    const isCategoryInUse = expenses.some(expense => expense.category === categoryToDelete);
    
    if (isCategoryInUse) {
      setError(`Cannot delete "${categoryToDelete}" because it's used in existing expenses`);
      return;
    }
    
    // Prevent deleting the "Other" category as it's a fallback
    if (categoryToDelete === 'Other') {
      setError('The "Other" category cannot be deleted as it serves as a fallback');
      return;
    }
    
    // Confirm deletion
    if (window.confirm(`Are you sure you want to delete the "${categoryToDelete}" category?`)) {
      setCategories(categories.filter(category => category !== categoryToDelete));
      setSuccess('Category deleted successfully');
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    }
  };
  
  const handleEditCategory = (oldCategory) => {
    setError('');
    setSuccess('');
    
    // Check if category is in use
    const isCategoryInUse = expenses.some(expense => expense.category === oldCategory);
    
    if (isCategoryInUse) {
      setError(`Cannot edit "${oldCategory}" because it's used in existing expenses`);
      return;
    }
    
    // Prevent editing the "Other" category
    if (oldCategory === 'Other') {
      setError('The "Other" category cannot be edited as it serves as a fallback');
      return;
    }
    
    // Prompt for new name
    const newName = prompt(`Enter new name for "${oldCategory}":`, oldCategory);
    
    if (!newName || newName.trim() === '') {
      return; // User cancelled or entered empty name
    }
    
    if (categories.includes(newName.trim()) && newName.trim() !== oldCategory) {
      setError('A category with this name already exists');
      return;
    }
    
    // Update the category
    setCategories(categories.map(category => 
      category === oldCategory ? newName.trim() : category
    ));
    setSuccess('Category updated successfully');
    
    // Clear success message after 3 seconds
    setTimeout(() => {
      setSuccess('');
    }, 3000);
  };
  
  return (
    <div className="category-management">
      <h2>Manage Expense Categories</h2>
      <p className="category-description">
        Customize your expense categories to better organize your spending.
      </p>
      
      {error && <div className="category-error">{error}</div>}
      {success && <div className="category-success">{success}</div>}
      
      <div className="add-category">
        <input
          type="text"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
          placeholder="New category name"
        />
        <button onClick={handleAddCategory}>Add Category</button>
      </div>
      
      <div className="categories-list">
        <h3>Your Categories</h3>
        {categories.map(category => (
          <div key={category} className="category-item">
            <span className="category-name">{category}</span>
            <div className="category-actions">
              <button 
                className="edit-category"
                onClick={() => handleEditCategory(category)}
                disabled={category === 'Other'}
              >
                Edit
              </button>
              <button 
                className="delete-category"
                onClick={() => handleDeleteCategory(category)}
                disabled={category === 'Other'}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryManagement;
