// src/context/ExpenseContext.js
import React, { createContext, useState, useEffect } from 'react';

export const ExpenseContext = createContext();

export const ExpenseProvider = ({ children }) => {
  const [expenses, setExpenses] = useState([]);
  const [incomes, setIncomes] = useState([]);
  const [budgets, setBudgets] = useState({});
  const [alerts, setAlerts] = useState([]);

  const { currentUser } = useContext(AuthContext);
  
  // Load data from localStorage on initial render
  useEffect(() => {
    const savedExpenses = JSON.parse(localStorage.getItem('expenses')) || [];
    const savedIncomes = JSON.parse(localStorage.getItem('incomes')) || [];
    const savedBudgets = JSON.parse(localStorage.getItem('budgets')) || {};
    
    setExpenses(savedExpenses);
    setIncomes(savedIncomes);
    setBudgets(savedBudgets);
  }, []);
  
  // Save data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('expenses', JSON.stringify(expenses));
  }, [expenses]);
  
  useEffect(() => {
    localStorage.setItem('incomes', JSON.stringify(incomes));
  }, [incomes]);
  
  useEffect(() => {
    localStorage.setItem('budgets', JSON.stringify(budgets));
  }, [budgets]);
  
  // Add a new expense
  const addExpense = (expense) => {
    const newExpense = {
      ...expense,
      id: Date.now(),
      date: expense.date || new Date().toISOString().slice(0, 10)
    };
    
    setExpenses([...expenses, newExpense]);
    checkBudgetAlerts(newExpense);
  };
  
  // Add a new income
  const addIncome = (income) => {
    const newIncome = {
      ...income,
      id: Date.now(),
      date: income.date || new Date().toISOString().slice(0, 10)
    };
    
    setIncomes([...incomes, newIncome]);
  };
  
  // Delete an expense
  const deleteExpense = (id) => {
    setExpenses(expenses.filter(expense => expense.id !== id));
  };
  
  // Delete an income
  const deleteIncome = (id) => {
    setIncomes(incomes.filter(income => income.id !== id));
  };
  
  // Update budget settings
  const updateBudget = (category, amount) => {
    setBudgets({
      ...budgets,
      [category]: amount
    });
  };
  
  // Check if adding this expense exceeds the budget
  const checkBudgetAlerts = (newExpense) => {
    const { category } = newExpense;
    
    if (budgets[category]) {
      // Get current month expenses for this category
      const currentDate = new Date();
      const currentMonth = currentDate.getMonth();
      const currentYear = currentDate.getFullYear();
      
      const monthlyExpenses = expenses.filter(expense => {
        const expenseDate = new Date(expense.date);
        return expenseDate.getMonth() === currentMonth && 
               expenseDate.getFullYear() === currentYear &&
               expense.category === category;
      });
      
      const totalSpent = monthlyExpenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0) + parseFloat(newExpense.amount);
      
      if (totalSpent > budgets[category]) {
        const newAlert = {
          id: Date.now(),
          message: `Budget alert: You've exceeded your ${category} budget of $${budgets[category]}`,
          category: category,
          read: false,
          date: new Date().toISOString()
        };
        
        setAlerts(prevAlerts => [...prevAlerts, newAlert]);
      }
    }
  };
  
  // Mark an alert as read
  const markAlertAsRead = (id) => {
    setAlerts(alerts.map(alert => 
      alert.id === id ? { ...alert, read: true } : alert
    ));
  };
  
  return (
    <ExpenseContext.Provider 
      value={{ 
        expenses, 
        addExpense, 
        deleteExpense, 
        incomes, // Make sure this is included
        addIncome,
        deleteIncome,
        budgets, 
        updateBudget,
        alerts,
        markAlertAsRead
      }}>
      {children}
    </ExpenseContext.Provider>
  );
};
