// src/components/notifications/NotificationCenter.js
import React, { useContext, useState } from 'react';
import { ExpenseContext } from '../../context/ExpenseContext';

const NotificationCenter = () => {
  const { alerts, markAlertAsRead } = useContext(ExpenseContext);
  const [isOpen, setIsOpen] = useState(false);
  
  const unreadAlerts = alerts.filter(alert => !alert.read);
  
  const toggleNotifications = () => {
    setIsOpen(!isOpen);
  };
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };
  
  const handleDismiss = (id) => {
    markAlertAsRead(id);
  };
  
  const handleDismissAll = () => {
    alerts.forEach(alert => {
      if (!alert.read) {
        markAlertAsRead(alert.id);
      }
    });
  };
  
  return (
    <div className="notification-center">
      <button 
        className={`notification-toggle ${unreadAlerts.length > 0 ? 'has-alerts' : ''}`}
        onClick={toggleNotifications}
      >
        <i className="notification-icon">ðŸ””</i>
        {unreadAlerts.length > 0 && <span className="notification-badge">{unreadAlerts.length}</span>}
      </button>
      
      {isOpen && (
        <div className="notification-dropdown">
          <div className="notification-header">
            <h3>Notifications</h3>
            {unreadAlerts.length > 0 && (
              <button className="dismiss-all-btn" onClick={handleDismissAll}>
                Dismiss All
              </button>
            )}
          </div>
          
          <div className="notification-list">
            {alerts.length > 0 ? (
              <ul>
                {alerts.map(alert => (
                  <li 
                    key={alert.id} 
                    className={`notification-item ${alert.read ? 'read' : 'unread'}`}
                    style={{ backgroundColor: alert.read ? 'white' : '#e6f7ff' }}
                  >
                    <div className="notification-content">
                      <p className="notification-message" style={{ color: '#000' }}>
                        {alert.message}
                      </p>
                      <span className="notification-date" style={{ color: '#000' }}>
                        {formatDate(alert.date)}
                      </span>
                    </div>
                    {!alert.read && (
                      <button 
                        className="dismiss-btn"
                        onClick={() => handleDismiss(alert.id)}
                      >
                        Dismiss
                      </button>
                    )}
                  </li>
                ))}
              </ul>
            ) : (
              <p className="no-notifications">No notifications</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default NotificationCenter;
