import emailjs from 'emailjs-com';

// Your EmailJS credentials
const EMAILJS_PUBLIC_KEY = 'g0Zv3mEDUVljCuJyL';  // This should be your public key
const EMAILJS_SERVICE_ID = 'service_uk2jyu5';
const EMAILJS_TEMPLATE_ID = 'template_kjrzsq6';

// Initialize EmailJS (only needed once in your application)
// Note: As of EmailJS v3, we should use init with the public key
emailjs.init(EMAILJS_PUBLIC_KEY);

/**
 * Sends a budget alert email when a user exceeds their budget
 * @param {object} user - The user object containing email and name
 * @param {string} category - The budget category that was exceeded
 * @param {number} budget - The budget amount
 * @param {number} spent - The amount spent
 * @returns {Promise<object>} - Result of the email sending operation
 */
export const sendBudgetAlertEmail = async (user, category, budget, spent) => {
  console.log('ðŸ“§ Attempting to send budget alert email...');
  console.log('ðŸ“§ Service ID:', EMAILJS_SERVICE_ID);
  console.log('ðŸ“§ Template ID:', EMAILJS_TEMPLATE_ID);
  
  // Verify that we have a user with an email address
  if (!user || !user.email) {
    console.error('ðŸ“§ Cannot send email: User email is missing', user);
    return {
      success: false,
      message: 'User email is missing'
    };
  }

  console.log(`ðŸ“§ Sending budget alert email to: ${user.email}`);
  
  const to = user.email;
  const userName = user.name || to.split('@')[0];
  const over_amount = (spent - budget).toFixed(2);
  
  // Create template parameters that EmailJS will use
  // These must match the variables in your EmailJS template
  const templateParams = {
    to_email: to,
    to_name: userName,
    category: category,
    budget: budget.toFixed(2),
    spent: spent.toFixed(2),
    over_amount: over_amount
  };
  
  console.log('ðŸ“§ Email template parameters:', templateParams);
  
  try {
    // Send email using EmailJS
    console.log('ðŸ“§ Sending email via EmailJS...');
    const response = await emailjs.send(
      EMAILJS_SERVICE_ID,
      EMAILJS_TEMPLATE_ID,
      templateParams
    );
    
    console.log('ðŸ“§ Email sent successfully!', response);
    console.log('ðŸ“§ Response status:', response.status);
    console.log('ðŸ“§ Response text:', response.text);
    
    return {
      success: true,
      message: 'Email sent successfully',
      response: response
    };
  } catch (error) {
    console.error('ðŸ“§ Error sending email:', error);
    
    // Provide detailed error information
    let errorMessage = 'Failed to send email';
    
    if (error.text) {
      errorMessage += `: ${error.text}`;
    }
    
    if (error.status) {
      errorMessage += ` (Status: ${error.status})`;
    }
    
    // Additional debugging for common issues
    if (error.message && error.message.includes('Network Error')) {
      console.error('ðŸ“§ Network issue detected. Check your internet connection.');
      errorMessage = 'Network error: Check your internet connection';
    }
    
    if (error.message && error.message.includes('Invalid ID format')) {
      console.error('ðŸ“§ EmailJS ID format issue detected.');
      errorMessage = 'EmailJS configuration error: Invalid ID format';
    }
    
    return {
      success: false,
      message: errorMessage,
      error: error
    };
  }
};

/**
 * Send a test email to verify the configuration
 * @param {object} user - The user object containing email and name
 * @returns {Promise<object>} - Result of the email sending operation
 */
