// src/services/EmailService.js
import emailjs from 'emailjs-com';

// Use environment variables instead of hardcoded values
// You should set these up in your .env file
const EMAILJS_USER_ID = process.env.REACT_APP_EMAILJS_USER_ID || 'g0Zv3mEDUVljCuJyL'; 
const EMAILJS_SERVICE_ID = process.env.REACT_APP_EMAILJS_SERVICE_ID || 'service_uk2jyu5';
const EMAILJS_TEMPLATE_ID = process.env.REACT_APP_EMAILJS_TEMPLATE_ID || 'template_kjrzsq6';

// Initialize EmailJS only if we have the user ID
if (EMAILJS_USER_ID !== 'YOUR_USER_ID') {
  emailjs.init(EMAILJS_USER_ID);
}

// Function to send budget alert email
export const sendBudgetAlertEmail = async (user, category, budget, spent) => {
  // Verify that we have a user with an email address
  if (!user || !user.email) {
    console.error('Cannot send email: User email is missing', user);
    return {
      success: false,
      message: 'User email is missing'
    };
  }
  
  // Check if EmailJS is properly configured
  if (EMAILJS_USER_ID === 'YOUR_USER_ID' || 
      EMAILJS_SERVICE_ID === 'YOUR_SERVICE_ID' || 
      EMAILJS_TEMPLATE_ID === 'YOUR_TEMPLATE_ID') {
    console.warn('EmailJS not configured. Please set up environment variables.');
    return {
      success: false,
      message: 'Email service not configured'
    };
  }
  
  console.log('Preparing to send budget alert email to:', user.email);
  
  const to = user.email; // This is the user's email from their account
  const over_amount = (spent - budget).toFixed(2);
  
  // Create template parameters that EmailJS will use
  // These must match the variables in your EmailJS template
  const templateParams = {
    to_email: to, // The recipient's email (user's email)
    to_name: user.name || to.split('@')[0],
    category: category,
    budget: budget.toFixed(2),
    spent: spent.toFixed(2),
    over_amount: over_amount,
    // If your EmailJS template uses different variable names, adjust them here
  };
  
  console.log('Email template parameters:', templateParams);
  
  try {
    // Send email using EmailJS
    const response = await emailjs.send(
      EMAILJS_SERVICE_ID,
      EMAILJS_TEMPLATE_ID,
      templateParams
    );
    
    console.log('Email sent successfully:', response);
    
    return {
      success: true,
      message: 'Email sent successfully'
    };
  } catch (error) {
    console.error('Error sending email:', error);
    return {
      success: false,
      message: 'Failed to send email',
      error: error.message
    };
  }
};
