// src/components/notifications/NotificationCenter.js
import React, { useContext, useState } from 'react';
import { ExpenseContext } from '../../context/ExpenseContext';

const NotificationCenter = () => {
  const { alerts, markAlertAsRead } = useContext(ExpenseContext);
  const [isOpen, setIsOpen] = useState(false);
  
  const unreadAlerts = alerts.filter(alert => !alert.read);
  
  const toggleNotifications = () => {
    setIsOpen(!isOpen);
  };
  
  // This is a simple implementation - you can expand this in the future
  return (
    <div className="notification-center" style={{ position: 'relative' }}>
      <button 
        onClick={toggleNotifications}
        style={{ 
          background: 'none',
          border: 'none',
          color: 'rgba(3, 3, 3, 0.8)',
          fontSize: '16px',
          cursor: 'pointer',
          position: 'relative'
        }}
      >
        ðŸ””
        {unreadAlerts.length > 0 && (
          <span style={{
            position: 'absolute',
            top: '-5px',
            right: '-5px',
            background: '#ff4d4f',
            color: 'white',
            borderRadius: '50%',
            width: '16px',
            height: '16px',
            fontSize: '12px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            {unreadAlerts.length}
          </span>
        )}
      </button>
      
      {isOpen && unreadAlerts.length > 0 && (
        <div style={{
          position: 'absolute',
          top: '100%',
          right: 0,
          width: '250px',
          background: 'white',
          borderRadius: '4px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
          zIndex: 1000
        }}>
          <div style={{ padding: '10px', borderBottom: '1px solid #eee' }}>
            <h4 style={{ margin: 0 }}>Notifications</h4>
          </div>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0, maxHeight: '300px', overflow: 'auto' }}>
            {unreadAlerts.map(alert => (
              <li key={alert.id} style={{ padding: '10px', borderBottom: '1px solid #eee' }}>
                <div>{alert.message}</div>
                <div style={{ fontSize: '12px', color: '#999', marginTop: '5px' }}>
                  {new Date(alert.date).toLocaleDateString()}
                </div>
                <button 
                  onClick={() => markAlertAsRead(alert.id)}
                  style={{
                    background: 'none',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    padding: '3px 8px',
                    fontSize: '12px',
                    marginTop: '5px',
                    cursor: 'pointer'
                  }}
                >
                  Dismiss
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default NotificationCenter;
