// src/context/ExpenseContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { AuthContext } from './AuthContext';
import { sendBudgetAlertEmail } from '../services/EmailService';
// Import the receipt scanning service
import { scanReceiptImage, parseReceiptData } from '../services/ReceiptScanningService';

export const ExpenseContext = createContext();

export const ExpenseProvider = ({ children }) => {
  const [expenses, setExpenses] = useState([]);
  const [incomes, setIncomes] = useState([]);
  const [budgets, setBudgets] = useState({});
  const [alerts, setAlerts] = useState([]);
  const [emailStatus, setEmailStatus] = useState({ sent: false, error: null });
  const [expenseCategories, setExpenseCategories] = useState([]);
  const [incomeCategories, setIncomeCategories] = useState([]);
  // Add new state for receipt scanning
  const [receiptScanStatus, setReceiptScanStatus] = useState({ 
    scanning: false, 
    error: null, 
    data: null 
  });
  // Track receipt scan history
  const [receiptScans, setReceiptScans] = useState([]);
  
  const { currentUser } = useContext(AuthContext);
  
  // Default categories if none are found
  const DEFAULT_EXPENSE_CATEGORIES = [
    'Food', 'Housing', 'Transportation', 'Entertainment', 
    'Utilities', 'Healthcare', 'Shopping', 'Personal Care', 'Education', 'Other'
  ];
  
  const DEFAULT_INCOME_CATEGORIES = [
    'Salary', 'Freelance', 'Investment', 'Gift', 
    'Business', 'Side Hustle', 'Rental', 'Other'
  ];
  
  // Generate unique storage keys for current user
  const getUserStorageKey = (key) => {
    if (!currentUser) return key;
    return `${key}_${currentUser.id}`;
  };
  
  // Load data from localStorage on initial render or when user changes
  useEffect(() => {
    if (currentUser) {
      try {
        // Load expenses
        const savedExpenses = JSON.parse(localStorage.getItem(getUserStorageKey('expenses'))) || [];
        setExpenses(Array.isArray(savedExpenses) ? savedExpenses : []);
        
        // Load incomes
        const savedIncomes = JSON.parse(localStorage.getItem(getUserStorageKey('incomes'))) || [];
        setIncomes(Array.isArray(savedIncomes) ? savedIncomes : []);
        
        // Load budgets
        const savedBudgets = JSON.parse(localStorage.getItem(getUserStorageKey('budgets'))) || {};
        setBudgets(typeof savedBudgets === 'object' ? savedBudgets : {});
        
        // Load alerts
        const savedAlerts = JSON.parse(localStorage.getItem(getUserStorageKey('alerts'))) || [];
        setAlerts(Array.isArray(savedAlerts) ? savedAlerts : []);
        
        // Load expense categories
        const savedExpenseCategories = JSON.parse(localStorage.getItem(getUserStorageKey('expenseCategories'))) || DEFAULT_EXPENSE_CATEGORIES;
        setExpenseCategories(Array.isArray(savedExpenseCategories) ? savedExpenseCategories : DEFAULT_EXPENSE_CATEGORIES);
        
        // Load income categories
        const savedIncomeCategories = JSON.parse(localStorage.getItem(getUserStorageKey('incomeCategories'))) || DEFAULT_INCOME_CATEGORIES;
        setIncomeCategories(Array.isArray(savedIncomeCategories) ? savedIncomeCategories : DEFAULT_INCOME_CATEGORIES);
        
        // Load receipt scans
        const savedReceiptScans = JSON.parse(localStorage.getItem(getUserStorageKey('receiptScans'))) || [];
        setReceiptScans(Array.isArray(savedReceiptScans) ? savedReceiptScans : []);
      } catch (error) {
        console.error('Error loading data from localStorage:', error);
        // Set defaults if there's an error
        setExpenses([]);
        setIncomes([]);
        setBudgets({});
        setAlerts([]);
        setExpenseCategories(DEFAULT_EXPENSE_CATEGORIES);
        setIncomeCategories(DEFAULT_INCOME_CATEGORIES);
        setReceiptScans([]);
      }
    } else {
      // Clear data when logged out
      setExpenses([]);
      setIncomes([]);
      setBudgets({});
      setAlerts([]);
      setExpenseCategories(DEFAULT_EXPENSE_CATEGORIES);
      setIncomeCategories(DEFAULT_INCOME_CATEGORIES);
      setReceiptScans([]);
    }
  }, [currentUser]);
  
  // Save data to localStorage whenever it changes
  useEffect(() => {
    if (currentUser && Array.isArray(expenses)) {
      localStorage.setItem(getUserStorageKey('expenses'), JSON.stringify(expenses));
    }
  }, [expenses, currentUser]);
  
  useEffect(() => {
    if (currentUser && Array.isArray(incomes)) {
      localStorage.setItem(getUserStorageKey('incomes'), JSON.stringify(incomes));
    }
  }, [incomes, currentUser]);
  
  useEffect(() => {
    if (currentUser && budgets && typeof budgets === 'object') {
      localStorage.setItem(getUserStorageKey('budgets'), JSON.stringify(budgets));
    }
  }, [budgets, currentUser]);
  
  useEffect(() => {
    if (currentUser && Array.isArray(alerts)) {
      localStorage.setItem(getUserStorageKey('alerts'), JSON.stringify(alerts));
    }
  }, [alerts, currentUser]);
  
  // Save categories to localStorage whenever they change
  useEffect(() => {
    if (currentUser && Array.isArray(expenseCategories) && expenseCategories.length > 0) {
      localStorage.setItem(getUserStorageKey('expenseCategories'), JSON.stringify(expenseCategories));
    }
  }, [expenseCategories, currentUser]);
  
  useEffect(() => {
    if (currentUser && Array.isArray(incomeCategories) && incomeCategories.length > 0) {
      localStorage.setItem(getUserStorageKey('incomeCategories'), JSON.stringify(incomeCategories));
    }
  }, [incomeCategories, currentUser]);
  
  // Save receipt scans to localStorage
  useEffect(() => {
    if (currentUser && Array.isArray(receiptScans)) {
      localStorage.setItem(getUserStorageKey('receiptScans'), JSON.stringify(receiptScans));
    }
  }, [receiptScans, currentUser]);
  
  // Clear email status after some time
  useEffect(() => {
    if (emailStatus.sent || emailStatus.error) {
      const timer = setTimeout(() => {
        setEmailStatus({ sent: false, error: null });
      }, 5000);
      
      return () => clearTimeout(timer);
    }
  }, [emailStatus]);
  
  // Clear receipt scan data after processing
  useEffect(() => {
    if (receiptScanStatus.data && !receiptScanStatus.scanning) {
      const timer = setTimeout(() => {
        setReceiptScanStatus(prevState => ({
          ...prevState, 
          data: null
        }));
      }, 30000); // Give user 30 seconds to review before clearing
      
      return () => clearTimeout(timer);
    }
  }, [receiptScanStatus]);
  
  // Add a new expense with validation
  const addExpense = (expense) => {
    if (!currentUser) return false; // Don't proceed if not logged in
    
    try {
      // Validate expense data
      if (!expense.category) {
        console.error('Missing category in expense');
        return false;
      }
      
      const amount = parseFloat(expense.amount);
      if (isNaN(amount) || amount <= 0) {
        console.error('Invalid expense amount:', expense.amount);
        return false;
      }
      
      // Ensure we have a valid date
      let expenseDate = expense.date;
      if (!expenseDate) {
        expenseDate = new Date().toISOString().slice(0, 10);
      }
      
      // Create new expense object
      const newExpense = {
        ...expense,
        amount: amount,
        id: Date.now(),
        date: expenseDate,
        userId: currentUser.id,
        // Add a flag to identify if this expense was added via receipt scanning
        fromReceipt: expense.fromReceipt || false,
        // Store receipt image reference if available
        receiptImageUrl: expense.receiptImageUrl || null,
        // Store receipt scan data ID if available
        receiptScanId: expense.receiptScanId || null
      };
      
      setExpenses(prevExpenses => {
        const updatedExpenses = [...prevExpenses, newExpense];
        
        // Check for budget alerts after state update
        setTimeout(() => {
          checkBudgetAlerts(newExpense, updatedExpenses);
        }, 0);
        
        return updatedExpenses;
      });
      
      return true;
    } catch (error) {
      console.error('Error adding expense:', error);
      return false;
    }
  };
  
  // New function to handle receipt scanning
  const scanReceipt = async (imageFile) => {
    if (!currentUser) return { success: false, error: 'User not logged in' };
    if (!imageFile) {
      setReceiptScanStatus({
        scanning: false,
        error: 'No image provided',
        data: null
      });
      return { success: false, error: 'No image provided' };
    }
    
    try {
      // Set scanning status
      setReceiptScanStatus({
        scanning: true,
        error: null,
        data: null
      });
      
      // Call the receipt scanning service
      const scanResult = await scanReceiptImage(imageFile);
      
      if (!scanResult || !scanResult.success) {
        const errorMessage = scanResult?.error || 'Failed to scan receipt';
        setReceiptScanStatus({
          scanning: false,
          error: errorMessage,
          data: null
        });
        
        return { success: false, error: errorMessage };
      }
      
      // Process the scanned data
      const parsedData = parseReceiptData(scanResult);
      
      if (!parsedData) {
        setReceiptScanStatus({
          scanning: false,
          error: 'Failed to parse receipt data',
          data: null
        });
        
        return { success: false, error: 'Failed to parse receipt data' };
      }
      
      // Create a scan record
      const scanId = Date.now();
      const scanRecord = {
        id: scanId,
        date: new Date().toISOString(),
        rawData: scanResult,
        parsedData: parsedData,
        userId: currentUser.id,
        status: 'pending', // pending, applied, rejected
        imageUrl: scanResult.imageUrl
      };
      
      // Store the scan record
      setReceiptScans(prevScans => [...prevScans, scanRecord]);
      
      // Prepare the extracted data for the UI
      const extractedExpense = {
        amount: parsedData.amount,
        description: parsedData.merchantName || 'Receipt expense',
        category: mapVendorToCategory(parsedData.merchantName || ''),
        date: parsedData.date || new Date().toISOString().slice(0, 10),
        fromReceipt: true,
        receiptImageUrl: parsedData.imageUrl || null,
        receiptScanId: scanId,
        notes: `Receipt from ${parsedData.merchantName || 'unknown vendor'}${parsedData.items?.length > 0 ? `, Items: ${parsedData.items.join(', ')}` : ''}`
      };
      
      // Update scan status with the extracted data
      setReceiptScanStatus({
        scanning: false,
        error: null,
        data: extractedExpense
      });
      
      return {
        success: true,
        data: extractedExpense,
        scanId
      };
    } catch (error) {
      console.error('Error scanning receipt:', error);
      
      setReceiptScanStatus({
        scanning: false,
        error: error.message || 'Error scanning receipt',
        data: null
      });
      
      return {
        success: false,
        error: error.message || 'Error scanning receipt'
      };
    }
  };
  
  // Function to add the expense from a scanned receipt
  const addExpenseFromReceipt = async (expenseData, modifications = {}) => {
    if (!currentUser) return false;
    
    try {
      // Combine scanned data with any user modifications
      const finalExpenseData = {
        ...expenseData,
        ...modifications,
        fromReceipt: true
      };
      
      // Use the existing addExpense function
      const result = addExpense(finalExpenseData);
      
      if (result && finalExpenseData.receiptScanId) {
        // Update the scan record status
        setReceiptScans(prevScans => 
          prevScans.map(scan => 
            scan.id === finalExpenseData.receiptScanId 
              ? { ...scan, status: 'applied' } 
              : scan
          )
        );
        
        // Clear the scanned data after successful addition
        setReceiptScanStatus(prevState => ({
          ...prevState,
          data: null
        }));
      }
      
      return result;
    } catch (error) {
      console.error('Error adding expense from receipt:', error);
      return false;
    }
  };
  
  // Function to reject a scanned receipt
  const rejectScannedReceipt = (scanId) => {
    if (!scanId) return false;
    
    try {
      // Update the scan record status
      setReceiptScans(prevScans => 
        prevScans.map(scan => 
          scan.id === scanId ? { ...scan, status: 'rejected' } : scan
        )
      );
      
      // Clear any pending scan data with this ID
      if (receiptScanStatus.data?.receiptScanId === scanId) {
        setReceiptScanStatus({
          scanning: false,
          error: null,
          data: null
        });
      }
      
      return true;
    } catch (error) {
      console.error('Error rejecting scanned receipt:', error);
      return false;
    }
  };
  
  // Helper function to map vendor names to appropriate categories
  const mapVendorToCategory = (vendorName) => {
    const vendorNameLower = vendorName.toLowerCase();
    
    // Simple mapping logic - can be expanded based on common vendors
    if (!vendorName) return 'Other';
    
    const categoryMappings = {
      'restaurant': 'Food',
      'cafÃ©': 'Food',
      'cafe': 'Food',
      'grocery': 'Food',
      'supermarket': 'Food',
      'market': 'Food',
      'gas': 'Transportation',
      'fuel': 'Transportation',
      'uber': 'Transportation',
      'lyft': 'Transportation',
      'taxi': 'Transportation',
      'pharmacy': 'Healthcare',
      'drug': 'Healthcare',
      'doctor': 'Healthcare',
      'clinic': 'Healthcare',
      'hospital': 'Healthcare',
      'rent': 'Housing',
      'mortgage': 'Housing',
      'netflix': 'Entertainment',
      'spotify': 'Entertainment',
      'cinema': 'Entertainment',
      'movie': 'Entertainment',
      'theater': 'Entertainment',
      'amazon': 'Shopping',
      'walmart': 'Shopping',
      'target': 'Shopping',
      'utility': 'Utilities',
      'electric': 'Utilities',
      'water': 'Utilities',
      'gas bill': 'Utilities',
      'phone': 'Utilities',
      'internet': 'Utilities',
      'school': 'Education',
      'college': 'Education',
      'university': 'Education',
      'tuition': 'Education',
      'book': 'Education',
      'haircut': 'Personal Care',
      'salon': 'Personal Care',
      'spa': 'Personal Care',
      'gym': 'Personal Care'
    };
    
    for (const [keyword, category] of Object.entries(categoryMappings)) {
      if (vendorNameLower.includes(keyword)) {
        return category;
      }
    }
    
    return 'Other';
  };
  
  // Add a new income with validation
  const addIncome = (income) => {
    if (!currentUser) return false;
    
    try {
      // Validate income data
      if (!income.source) {
        console.error('Missing source in income');
        return false;
      }
      
      const amount = parseFloat(income.amount);
      if (isNaN(amount) || amount <= 0) {
        console.error('Invalid income amount:', income.amount);
        return false;
      }
      
      // Ensure we have a valid date
      let incomeDate = income.date;
      if (!incomeDate) {
        incomeDate = new Date().toISOString().slice(0, 10);
      }
      
      // Create new income object
      const newIncome = {
        ...income,
        amount: amount,
        id: Date.now(),
        date: incomeDate,
        userId: currentUser.id
      };
      
      setIncomes(prevIncomes => [...prevIncomes, newIncome]);
      return true;
    } catch (error) {
      console.error('Error adding income:', error);
      return false;
    }
  };
  
  // Delete an expense
  const deleteExpense = (id) => {
    if (!id) return false;
    
    try {
      // Get the expense before deleting it
      const expenseToDelete = expenses.find(expense => expense.id === id);
      
      setExpenses(expenses.filter(expense => expense.id !== id));
      
      // If this was a receipt-based expense, update the scan record
      if (expenseToDelete && expenseToDelete.receiptScanId) {
        setReceiptScans(prevScans => 
          prevScans.map(scan => 
            scan.id === expenseToDelete.receiptScanId 
              ? { ...scan, status: 'rejected' } 
              : scan
          )
        );
      }
      
      return true;
    } catch (error) {
      console.error('Error deleting expense:', error);
      return false;
    }
  };
  
  // Delete an income
  const deleteIncome = (id) => {
    if (!id) return false;
    
    try {
      setIncomes(incomes.filter(income => income.id !== id));
      return true;
    } catch (error) {
      console.error('Error deleting income:', error);
      return false;
    }
  };
  
  // Update budget settings
  const updateBudget = (category, amount) => {
    if (!category || category.trim() === '' || isNaN(parseFloat(amount))) {
      return false;
    }
    
    try {
      setBudgets(prevBudgets => ({
        ...prevBudgets,
        [category]: parseFloat(amount)
      }));
      return true;
    } catch (error) {
      console.error('Error updating budget:', error);
      return false;
    }
  };
  
  // Delete a budget
  const deleteBudget = (category) => {
    if (!category) return false;
    
    try {
      const updatedBudgets = { ...budgets };
      delete updatedBudgets[category];
      setBudgets(updatedBudgets);
      return true;
    } catch (error) {
      console.error('Error deleting budget:', error);
      return false;
    }
  };
  
  // Check if adding this expense exceeds the budget
  const checkBudgetAlerts = (newExpense, allExpenses = expenses) => {
    if (!currentUser) return; // Don't process if no user is logged in
    if (!newExpense || !newExpense.category) return;
    
    const { category } = newExpense;
    
    // Check if there's a budget for this category
    if (budgets[category]) {
      try {
        // Get current month expenses for this category
        const currentDate = new Date();
        const currentMonth = currentDate.getMonth();
        const currentYear = currentDate.getFullYear();
        
        const monthlyExpenses = allExpenses.filter(expense => {
          if (!expense.date) return false;
          
          try {
            const expenseDate = new Date(expense.date);
            return expenseDate.getMonth() === currentMonth && 
                  expenseDate.getFullYear() === currentYear &&
                  expense.category === category &&
                  expense.userId === currentUser.id;
          } catch (e) {
            console.error('Invalid date format:', expense.date);
            return false;
          }
        });
        
        const totalSpent = monthlyExpenses.reduce((sum, expense) => {
          const amount = parseFloat(expense.amount);
          return sum + (isNaN(amount) ? 0 : amount);
        }, 0);
        
        const budgetAmount = parseFloat(budgets[category]);
        
        if (!isNaN(budgetAmount) && totalSpent > budgetAmount) {
          // Create new alert
          const newAlert = {
            id: Date.now(),
            message: `Budget alert: You've exceeded your ${category} budget of $${budgetAmount.toFixed(2)}`,
            category: category,
            read: false,
            date: new Date().toISOString(),
            userId: currentUser.id
          };
          
          setAlerts(prevAlerts => [...prevAlerts, newAlert]);
          
          // Send email alert if user has enabled email notifications
          if (currentUser?.preferences?.emailNotifications !== false) {
            if (currentUser.email) {
              sendBudgetAlertEmail(
                currentUser,
                category,
                budgetAmount,
                totalSpent
              ).then(result => {
                setEmailStatus({ 
                  sent: result.success, 
                  error: result.success ? null : result.message 
                });
              }).catch(error => {
                setEmailStatus({ 
                  sent: false, 
                  error: error.message || 'Error sending email'
                });
              });
            } else {
              setEmailStatus({ 
                sent: false, 
                error: "No email address found for user"
              });
            }
          }
        }
      } catch (error) {
        console.error('Error checking budget alerts:', error);
      }
    }
  };
  
  // Mark an alert as read
  const markAlertAsRead = (id) => {
    if (!id) return false;
    
    try {
      setAlerts(prevAlerts => 
        prevAlerts.map(alert => 
          alert.id === id ? { ...alert, read: true } : alert
        )
      );
      return true;
    } catch (error) {
      console.error('Error marking alert as read:', error);
      return false;
    }
  };
  
  // Delete an alert
  const deleteAlert = (id) => {
    if (!id) return false;
    
    try {
      setAlerts(alerts.filter(alert => alert.id !== id));
      return true;
    } catch (error) {
      console.error('Error deleting alert:', error);
      return false;
    }
  };
  
  // Add a new expense category
  const addExpenseCategory = (category) => {
    if (!category || category.trim() === '') return false;
    if (expenseCategories.includes(category)) return false;
    
    try {
      setExpenseCategories([...expenseCategories, category]);
      return true;
    } catch (error) {
      console.error('Error adding expense category:', error);
      return false;
    }
  };
  
  // Delete an expense category
  const deleteExpenseCategory = (category) => {
    if (!category) return false;
    
    try {
      // Don't delete if there are expenses using this category
      const expensesUsingCategory = expenses.some(expense => expense.category === category);
      if (expensesUsingCategory) {
        return { success: false, reason: 'Category in use by existing expenses' };
      }
      
      // Remove from categories
      setExpenseCategories(expenseCategories.filter(cat => cat !== category));
      
      // Also remove any budgets for this category
      if (budgets[category]) {
        const updatedBudgets = { ...budgets };
        delete updatedBudgets[category];
        setBudgets(updatedBudgets);
      }
      
      return { success: true };
    } catch (error) {
      console.error('Error deleting expense category:', error);
      return { success: false, reason: error.message };
    }
  };
  
  // Edit an expense category
  const editExpenseCategory = (oldCategory, newCategory) => {
    if (!oldCategory || !newCategory || newCategory.trim() === '') return false;
    
    try {
      // Don't allow editing the "Other" category
      if (oldCategory === 'Other') return false;
      
      // Check if the new category name already exists
      if (expenseCategories.includes(newCategory) && newCategory !== oldCategory) return false;
      
      // Check if the category is in use
      const isCategoryInUse = expenses.some(expense => expense.category === oldCategory);
      if (isCategoryInUse) return false;
      
      setExpenseCategories(expenseCategories.map(cat => cat === oldCategory ? newCategory : cat));
      
      // Update any budget for this category
      if (budgets[oldCategory]) {
        const budgetAmount = budgets[oldCategory];
        deleteBudget(oldCategory);
        updateBudget(newCategory, budgetAmount);
      }
      
      return true;
    } catch (error) {
      console.error('Error editing expense category:', error);
      return false;
    }
  };
  
  // Add a new income category
  const addIncomeCategory = (category) => {
    if (!category || category.trim() === '') return false;
    if (incomeCategories.includes(category)) return false;
    
    try {
      setIncomeCategories([...incomeCategories, category]);
      return true;
    } catch (error) {
      console.error('Error adding income category:', error);
      return false;
    }
  };
  
  // Delete an income category
  const deleteIncomeCategory = (category) => {
    if (!category) return false;
    
    try {
      // Don't delete if there are incomes using this category
      const incomesUsingCategory = incomes.some(income => income.source === category);
      if (incomesUsingCategory) {
        return { success: false, reason: 'Category in use by existing incomes' };
      }
      
      // Remove from categories
      setIncomeCategories(incomeCategories.filter(cat => cat !== category));
      
      return { success: true };
    } catch (error) {
      console.error('Error deleting income category:', error);
      return { success: false, reason: error.message };
    }
  };
  
  // Edit an income category
  const editIncomeCategory = (oldCategory, newCategory) => {
    if (!oldCategory || !newCategory || newCategory.trim() === '') return false;
    
    try {
      // Don't allow editing the "Other" category
      if (oldCategory === 'Other') return false;
      
      // Check if the new category name already exists
      if (incomeCategories.includes(newCategory) && newCategory !== oldCategory) return false;
      
      // Check if the category is in use
      const isCategoryInUse = incomes.some(income => income.source === oldCategory);
      if (isCategoryInUse) return false;
      
      setIncomeCategories(incomeCategories.map(cat => cat === oldCategory ? newCategory : cat));
      return true;
    } catch (error) {
      console.error('Error editing income category:', error);
      return false;
    }
  };
  
  // Get expenses for a specific month
  const getMonthlyExpenses = (month, year) => {
    if (!currentUser) return [];
    
    const targetDate = new Date(year, month);
    const targetMonth = targetDate.getMonth();
    const targetYear = targetDate.getFullYear();
    
    return expenses.filter(expense => {
      if (!expense.date) return false;
      
      try {
        const expenseDate = new Date(expense.date);
        return expenseDate.getMonth() === targetMonth && 
              expenseDate.getFullYear() === targetYear &&
              expense.userId === currentUser.id;
      } catch (e) {
        return false;
      }
    });
  };
  
  // Get incomes for a specific month
  const getMonthlyIncomes = (month, year) => {
    if (!currentUser) return [];
    
    const targetDate = new Date(year, month);
    const targetMonth = targetDate.getMonth();
    const targetYear = targetDate.getFullYear();
    
    return incomes.filter(income => {
      if (!income.date) return false;
      
      try {
        const incomeDate = new Date(income.date);
        return incomeDate.getMonth() === targetMonth && 
              incomeDate.getFullYear() === targetYear &&
              income.userId === currentUser.id;
      } catch (e) {
        return false;
      }
    });
  };
  
  // Get historical receipt scans
  const getReceiptScans = () => {
    if (!currentUser) return [];
    
    return receiptScans.filter(scan => scan.userId === currentUser.id);
  };
  
  // Update an existing expense
  const updateExpense = (id, updatedData) => {
    if (!id || !updatedData) return false;
    
    try {
      setExpenses(prevExpenses => 
        prevExpenses.map(expense => 
          expense.id === id 
            ? { 
                ...expense, 
                ...updatedData,
                // Ensure amount is properly parsed
                amount: updatedData.amount ? parseFloat(updatedData.amount) : expense.amount
              } 
            : expense
        )
      );
      return true;
    } catch (error) {
      console.error('Error updating expense:', error);
      return false;
    }
  };
  
  // Update an existing income
  const updateIncome = (id, updatedData) => {
    if (!id || !updatedData) return false;
    
    try {
      setIncomes(prevIncomes => 
        prevIncomes.map(income => 
          income.id === id 
            ? { 
                ...income, 
                ...updatedData,
                // Ensure amount is properly parsed
                amount: updatedData.amount ? parseFloat(updatedData.amount) : income.amount
              } 
            : income
        )
      );
      return true;
    } catch (error) {
      console.error('Error updating income:', error);
      return false;
    }
  };
  
  // Export data for backups
  const exportData = () => {
    if (!currentUser) return null;
    
    try {
      const exportData = {
        expenses: expenses.filter(expense => expense.userId === currentUser.id),
        incomes: incomes.filter(income => income.userId === currentUser.id),
        budgets,
        expenseCategories,
        incomeCategories,
        receiptScans: receiptScans.filter(scan => scan.userId === currentUser.id),
        exportDate: new Date().toISOString(),
        userId: currentUser.id
      };
      
      return exportData;
    } catch (error) {
      console.error('Error exporting data:', error);
      return null;
    }
  };
  
  // Import data from backups
  const importData = (data) => {
    if (!currentUser || !data) return false;
    
    try {
      // Validate data structure
      if (!data.expenses || !data.incomes || !data.budgets || 
          !data.expenseCategories || !data.incomeCategories) {
        console.error('Invalid import data structure');
        return false;
      }
      
      // Update all data with import
      // Ensure all imported items have the correct user ID
      const processedExpenses = Array.isArray(data.expenses) 
        ? data.expenses.map(expense => ({...expense, userId: currentUser.id}))
        : [];
        
      const processedIncomes = Array.isArray(data.incomes)
        ? data.incomes.map(income => ({...income, userId: currentUser.id}))
        : [];
        
      const processedReceiptScans = Array.isArray(data.receiptScans)
        ? data.receiptScans.map(scan => ({...scan, userId: currentUser.id}))
        : [];
      
      setExpenses(processedExpenses);
      setIncomes(processedIncomes);
      setBudgets(data.budgets);
      setExpenseCategories(data.expenseCategories);
      setIncomeCategories(data.incomeCategories);
      setReceiptScans(processedReceiptScans);
      
      return true;
    } catch (error) {
      console.error('Error importing data:', error);
      return false;
    }
  };
  
  // Get spending insights
  const getSpendingInsights = (months = 3) => {
    if (!currentUser || months < 1) return null;
    
    try {
      const currentDate = new Date();
      const insights = {
        topCategories: [],
        monthlyTotals: [],
        yearlyComparison: {},
        receiptUsage: {
          total: 0,
          percentage: 0
        }
      };
      
      // Process expenses for insights
      const relevantExpenses = expenses.filter(expense => {
        if (!expense.date || expense.userId !== currentUser.id) return false;
        
        const expenseDate = new Date(expense.date);
        const monthsDiff = (currentDate.getFullYear() - expenseDate.getFullYear()) * 12 + 
                           (currentDate.getMonth() - expenseDate.getMonth());
        
        return monthsDiff < months;
      });
      
      // Calculate top spending categories
      const categoryTotals = {};
      relevantExpenses.forEach(expense => {
        const { category, amount } = expense;
        if (!category) return;
        
        categoryTotals[category] = (categoryTotals[category] || 0) + parseFloat(amount || 0);
      });
      
      insights.topCategories = Object.entries(categoryTotals)
        .map(([category, total]) => ({ category, total }))
        .sort((a, b) => b.total - a.total)
        .slice(0, 5); // Get top 5
      
      // Calculate monthly totals
      const monthlyData = {};
      relevantExpenses.forEach(expense => {
        if (!expense.date) return;
        
        const expenseDate = new Date(expense.date);
        const monthYear = `${expenseDate.getFullYear()}-${expenseDate.getMonth()+1}`;
        
        if (!monthlyData[monthYear]) {
          monthlyData[monthYear] = {
            month: expenseDate.getMonth(),
            year: expenseDate.getFullYear(),
            total: 0,
            label: expenseDate.toLocaleString('default', { month: 'short', year: 'numeric' })
          };
        }
        
        monthlyData[monthYear].total += parseFloat(expense.amount || 0);
      });
      
      insights.monthlyTotals = Object.values(monthlyData)
        .sort((a, b) => {
          if (a.year !== b.year) return a.year - b.year;
          return a.month - b.month;
        });
      
      // Calculate receipt usage statistics
      const receiptsCount = relevantExpenses.filter(expense => expense.fromReceipt).length;
      insights.receiptUsage = {
        total: receiptsCount,
        percentage: relevantExpenses.length > 0 
          ? (receiptsCount / relevantExpenses.length) * 100 
          : 0
      };
      
      return insights;
    } catch (error) {
      console.error('Error generating spending insights:', error);
      return null;
    }
  };
  
  // Value object to be provided by the context
  const value = {
    expenses,
    incomes,
    budgets,
    alerts,
    expenseCategories,
    incomeCategories,
    receiptScanStatus,
    receiptScans,
    emailStatus,
    addExpense,
    addIncome,
    deleteExpense,
    deleteIncome,
    updateExpense,
    updateIncome,
    updateBudget,
    deleteBudget,
    markAlertAsRead,
    deleteAlert,
    addExpenseCategory,
    deleteExpenseCategory,
    editExpenseCategory,
    addIncomeCategory,
    deleteIncomeCategory,
    editIncomeCategory,
    scanReceipt,
    addExpenseFromReceipt,
    rejectScannedReceipt,
    getMonthlyExpenses,
    getMonthlyIncomes,
    getReceiptScans,
    exportData,
    importData,
    getSpendingInsights
  };
  
  return (
    <ExpenseContext.Provider value={value}>
      {children}
    </ExpenseContext.Provider>
  );
};

export default ExpenseContext;
