// src/context/SplitExpenseContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { AuthContext } from './AuthContext';
import { ExpenseContext } from './ExpenseContext';
import { 
  createSplitExpense, 
  createParticipant, 
  updateParticipantStatus, 
  calculateSplitStatus 
} from '../models/SplitExpenseModel';
import { sendSplitExpenseEmail } from '../services/EmailService';

export const SplitExpenseContext = createContext();

export const SplitExpenseProvider = ({ children }) => {
  const [splitExpenses, setSplitExpenses] = useState([]);
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [emailStatus, setEmailStatus] = useState({});
  
  const { currentUser } = useContext(AuthContext);
  const { addExpense } = useContext(ExpenseContext);
  
  // Generate unique storage keys for current user
  const getUserStorageKey = (key) => {
    if (!currentUser) return key;
    return `${key}_${currentUser.id}`;
  };
  
  // Load data from localStorage when component mounts or user changes
  useEffect(() => {
    if (currentUser) {
      try {
        // Load split expenses
        const savedSplitExpenses = JSON.parse(localStorage.getItem(getUserStorageKey('splitExpenses'))) || [];
        setSplitExpenses(Array.isArray(savedSplitExpenses) ? savedSplitExpenses : []);
        
        // Load contacts
        const savedContacts = JSON.parse(localStorage.getItem(getUserStorageKey('splitContacts'))) || [];
        setContacts(Array.isArray(savedContacts) ? savedContacts : []);
      } catch (error) {
        console.error('Error loading split expense data:', error);
        setSplitExpenses([]);
        setContacts([]);
      } finally {
        setLoading(false);
      }
    } else {
      setSplitExpenses([]);
      setContacts([]);
      setLoading(false);
    }
  }, [currentUser]);
  
  // Save split expenses to localStorage whenever they change
  useEffect(() => {
    if (currentUser && !loading) {
      localStorage.setItem(getUserStorageKey('splitExpenses'), JSON.stringify(splitExpenses));
    }
  }, [splitExpenses, currentUser, loading]);
  
  // Save contacts to localStorage whenever they change
  useEffect(() => {
    if (currentUser && !loading) {
      localStorage.setItem(getUserStorageKey('splitContacts'), JSON.stringify(contacts));
    }
  }, [contacts, currentUser, loading]);
  
  /**
   * Create a new split expense
   * @param {Object} expenseData - Expense data (amount, category, etc.)
   * @param {Array} participants - Array of participants
   * @returns {Object} New split expense or null if failed
   */
  // Updated createNewSplitExpense function with email fixes
  const createNewSplitExpense = (expenseData, participants) => {
    if (!currentUser) return null;
    
    try {
      // First create the base expense
      const expenseResult = addExpense({
        ...expenseData,
        // Mark this as a split expense
        isSplit: true,
        notes: expenseData.notes || `Split with ${participants.length} people`
      });
      
      if (!expenseResult) {
        throw new Error('Failed to create base expense');
      }
      
      // Create the split expense object
      const newSplitExpense = createSplitExpense(
        {
          ...expenseData,
          // The addExpense function returns the ID, but we need the full object
          id: expenseResult
        },
        participants
      );
      
      // Add to state
      setSplitExpenses(prev => [...prev, newSplitExpense]);
      
      // Add any new contacts to the contacts list
      const existingContactEmails = contacts.map(c => c.email).filter(Boolean);
      const newContacts = participants
        .filter(p => p.email && !existingContactEmails.includes(p.email))
        .map(p => ({
          id: p.id,
          name: p.name,
          email: p.email
        }));
        
      if (newContacts.length > 0) {
        setContacts(prev => [...prev, ...newContacts]);
      }
      
      // Send email notifications to participants (except the creator)
      const otherParticipants = participants.filter(p => 
        p.email && p.email !== currentUser.email
      );
      
      if (otherParticipants.length > 0) {
        console.log(`Sending split expense notifications to ${otherParticipants.length} participants`);
        
        // Important: Process emails one by one with a small delay to avoid rate limits
        otherParticipants.forEach((participant, index) => {
          // Small delay between emails to prevent EmailJS rate limiting
          setTimeout(() => {
            // Track email status
            setEmailStatus(prev => ({
              ...prev,
              [participant.id]: { sending: true }
            }));
            
            // Make sure we have the required fields for the email template
            const emailParams = {
              to_email: participant.email,
              to_name: participant.name || 'Participant',
              creator_name: currentUser.name || 'Group member',
              expense_description: newSplitExpense.description || 'Split expense',
              expense_category: newSplitExpense.category || 'Uncategorized',
              expense_date: new Date(newSplitExpense.date).toLocaleDateString(),
              expense_amount: newSplitExpense.totalAmount.toFixed(2),
              share_amount: participant.shareAmount.toFixed(2),
              is_reminder: false,
              reminder_message: '',
              // Add these for From Name and Reply To in the template
              name: currentUser.name || 'FinTrack',
              email: currentUser.email || ''
            };
            
            // Send the email with proper parameters
            sendSplitExpenseEmail(
              participant, 
              newSplitExpense, 
              currentUser,
              false,
              emailParams
            )
              .then(result => {
                console.log(`Email result for ${participant.name}:`, result);
                
                // Update email status
                setEmailStatus(prev => ({
                  ...prev,
                  [participant.id]: { 
                    sent: result.success, 
                    error: result.success ? null : result.message 
                  }
                }));
              })
              .catch(error => {
                console.error(`Failed to send split email to ${participant.name}:`, error);
                
                // Update email status
                setEmailStatus(prev => ({
                  ...prev,
                  [participant.id]: { 
                    sent: false, 
                    error: error.message || 'Unknown error' 
                  }
                }));
              });
          }, index * 1000); // 1 second delay between emails
        });
        
        // Clear email status after some time
        setTimeout(() => {
          setEmailStatus({});
        }, (otherParticipants.length + 1) * 2000); // Wait longer than the email sending process
      }
      
      return newSplitExpense;
    } catch (error) {
      console.error('Error creating split expense:', error);
      return null;
    }
  };
  
  /**
   * Get a split expense by ID
   * @param {string|number} id - Split expense ID
   * @returns {Object} Split expense or null if not found
   */
  const getSplitExpense = (id) => {
    return splitExpenses.find(expense => expense.id === id) || null;
  };
  
  /**
   * Update a participant's payment status
   * @param {string|number} splitExpenseId - Split expense ID
   * @param {string} participantId - Participant ID
   * @param {string} status - New status (paid, unpaid, declined)
   * @param {string} paymentMethod - Method of payment
   * @returns {boolean} Success status
   */
  const updatePaymentStatus = (splitExpenseId, participantId, status, paymentMethod) => {
    try {
      // Find the split expense
      const updatedSplitExpenses = splitExpenses.map(splitExpense => {
        if (splitExpense.id !== splitExpenseId) return splitExpense;
        
        // Update the participant
        const updatedParticipants = splitExpense.participants.map(participant => {
          if (participant.id !== participantId) return participant;
          return updateParticipantStatus(participant, status, paymentMethod);
        });
        
        // Recalculate the overall status
        const updatedSplitExpense = {
          ...splitExpense,
          participants: updatedParticipants,
          status: calculateSplitStatus({
            ...splitExpense,
            participants: updatedParticipants
          })
        };
        
        return updatedSplitExpense;
      });
      
      setSplitExpenses(updatedSplitExpenses);
      return true;
    } catch (error) {
      console.error('Error updating payment status:', error);
      return false;
    }
  };
  
  /**
   * Add a new contact
   * @param {string} name - Contact name
   * @param {string} email - Contact email
   * @returns {Object} New contact or null if failed
   */
  const addContact = (name, email) => {
    if (!name.trim()) return null;
    
    try {
      // Check if contact already exists
      if (contacts.some(c => c.email === email)) {
        return null;
      }
      
      const newContact = {
        id: `c_${Date.now()}`,
        name: name.trim(),
        email: email?.trim() || ''
      };
      
      setContacts(prev => [...prev, newContact]);
      return newContact;
    } catch (error) {
      console.error('Error adding contact:', error);
      return null;
    }
  };
  
  /**
   * Delete a contact
   * @param {string} contactId - Contact ID
   * @returns {boolean} Success status
   */
  const deleteContact = (contactId) => {
    try {
      setContacts(prev => prev.filter(contact => contact.id !== contactId));
      return true;
    } catch (error) {
      console.error('Error deleting contact:', error);
      return false;
    }
  };
  
  /**
   * Get all split expenses
   * @returns {Array} Array of split expenses
   */
  const getAllSplitExpenses = () => {
    return splitExpenses;
  };
  
  /**
   * Get all contacts
   * @returns {Array} Array of contacts
   */
  const getAllContacts = () => {
    return contacts;
  };
  
  /**
   * Delete a split expense
   * @param {string|number} splitExpenseId - Split expense ID
   * @returns {boolean} Success status
   */
  const deleteSplitExpense = (splitExpenseId) => {
    try {
      // Remove from state
      setSplitExpenses(prev => prev.filter(expense => expense.id !== splitExpenseId));
      return true;
    } catch (error) {
      console.error('Error deleting split expense:', error);
      return false;
    }
  };
  
  /**
   * Remind a participant to pay
   * @param {string|number} splitExpenseId - Split expense ID
   * @param {string} participantId - Participant ID
   * @returns {boolean} Success status
   */
  const sendPaymentReminder = (splitExpenseId, participantId) => {
    try {
      const splitExpense = getSplitExpense(splitExpenseId);
      if (!splitExpense) return false;
      
      const participant = splitExpense.participants.find(p => p.id === participantId);
      if (!participant || !participant.email) return false;
      
      // Set status to sending
      setEmailStatus(prev => ({
        ...prev,
        [participantId]: { sending: true }
      }));
      
      // Send reminder email
      sendSplitExpenseEmail(participant, splitExpense, currentUser, true)
        .then(result => {
          console.log(`Reminder email result for ${participant.name}:`, result);
          
          // Update email status
          setEmailStatus(prev => ({
            ...prev,
            [participantId]: { 
              sent: result.success, 
              error: result.success ? null : result.message 
            }
          }));
          
          // Clear status after some time
          setTimeout(() => {
            setEmailStatus(prev => {
              const newStatus = { ...prev };
              delete newStatus[participantId];
              return newStatus;
            });
          }, 5000);
        })
        .catch(error => {
          console.error(`Failed to send reminder email to ${participant.name}:`, error);
          
          // Update email status
          setEmailStatus(prev => ({
            ...prev,
            [participantId]: { 
              sent: false, 
              error: error.message || 'Unknown error' 
            }
          }));
          
          // Clear status after some time
          setTimeout(() => {
            setEmailStatus(prev => {
              const newStatus = { ...prev };
              delete newStatus[participantId];
              return newStatus;
            });
          }, 5000);
        });
      
      return true;
    } catch (error) {
      console.error('Error sending payment reminder:', error);
      return false;
    }
  };
  
  /**
   * Create a participant from a contact
   * @param {Object} contact - Contact object
   * @returns {Object} Participant object
   */
  const createParticipantFromContact = (contact) => {
    return createParticipant(contact.name, contact.email);
  };
  
  /**
   * Get the email status for a participant
   * @param {string} participantId - Participant ID
   * @returns {Object} Email status object or null
   */
  const getEmailStatus = (participantId) => {
    return emailStatus[participantId] || null;
  };
  
  return (
    <SplitExpenseContext.Provider
      value={{
        loading,
        createNewSplitExpense,
        getSplitExpense,
        updatePaymentStatus,
        addContact,
        deleteContact,
        getAllSplitExpenses,
        getAllContacts,
        deleteSplitExpense,
        sendPaymentReminder,
        createParticipant,
        createParticipantFromContact,
        emailStatus,
        getEmailStatus
      }}
    >
      {children}
    </SplitExpenseContext.Provider>
