import React, { useContext, useState } from 'react';
import { ExpenseContext } from '../context/ExpenseContext';
import './TransactionHistory.css';

const TransactionHistory = () => {
  const { expenses, deleteExpense } = useContext(ExpenseContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  
  const categories = [
    'Food', 'Housing', 'Transportation', 'Entertainment', 
    'Utilities', 'Healthcare', 'Shopping', 'Personal Care', 'Education', 'Other'
  ];
  
  const filteredExpenses = expenses
    .filter(expense => 
      expense.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      expense.category.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .filter(expense => 
      filterCategory ? expense.category === filterCategory : true
    )
    .sort((a, b) => new Date(b.date) - new Date(a.date));
  
  return (
    <div className="transaction-history">
      <h1>Transaction History</h1>
      
      <div className="filters">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search transactions..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        
        <div className="category-filter">
          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
          >
            <option value="">All Categories</option>
            {categories.map((category) => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>
      </div>
      
      {filteredExpenses.length > 0 ? (
        <table className="expenses-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Category</th>
              <th>Description</th>
              <th>Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredExpenses.map(expense => (
              <tr key={expense.id}>
                <td>{expense.date}</td>
                <td>{expense.category}</td>
                <td>{expense.description}</td>
                <td className="amount">${parseFloat(expense.amount).toFixed(2)}</td>
                <td>
                  <button 
                    className="delete-btn"
                    onClick={() => deleteExpense(expense.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="no-results">No transactions found.</p>
      )}
    </div>
  );
};

export default TransactionHistory;
