// src/config/firebase.js
import { initializeApp } from 'firebase/app';
import { getAuth } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';
import { getStorage } from 'firebase/storage';

// Use a direct configuration instead of environment variables for debugging
const firebaseConfig = {
  apiKey: "AIzaSyBTbRyIaR3l-bqM70pvy86gkpS8kWF0FX8",
  authDomain: "expensy-forge.firebaseapp.com",
  projectId: "expensy-forge",
  storageBucket: "expensy-forge.firebasestorage.app",
  messagingSenderId: "763967536539",
  appId: "1:763967536539:web:2c380eb30bd4e4a9a5b53e",
  measurementId: "G-CQY4M9MDC1"
};

// Initialize Firebase
console.log("Initializing Firebase with config:", JSON.stringify(firebaseConfig));
const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);
export const db = getFirestore(app);
export const storage = getStorage(app);

console.log("Firebase initialization complete");

export default app;
