import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ExpenseContext } from '../context/ExpenseContext';
import { createWorker } from 'tesseract.js'; // Import Tesseract
import './ReceiptScanner.css';

const ReceiptScanner = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [error, setError] = useState('');
  const [ocrProgress, setOcrProgress] = useState(0); // Track OCR progress
  
  // Local state for form values
  const [receiptData, setReceiptData] = useState({
    amount: '',
    date: new Date().toISOString().slice(0, 10),
    description: '',
    category: 'Food',
    notes: ''
  });
  
  const { scanReceipt, addExpenseFromReceipt, receiptScanStatus, expenseCategories } = useContext(ExpenseContext);
  const navigate = useNavigate();
  
  // Update local state when receiptScanStatus.data changes
  useEffect(() => {
    if (receiptScanStatus.data) {
      setReceiptData({
        amount: receiptScanStatus.data.amount || '',
        date: receiptScanStatus.data.date || new Date().toISOString().slice(0, 10),
        description: receiptScanStatus.data.description || '',
        category: receiptScanStatus.data.category || 'Food',
        notes: receiptScanStatus.data.notes || ''
      });
    }
  }, [receiptScanStatus.data]);
  
  const handleFileChange = (e) => {
    setError(''); // Clear any previous errors
    
    const file = e.target.files[0];
    if (file) {
      // Validate file size (limit to 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('File is too large. Please select an image under 5MB.');
        return;
      }
      
      // Validate file type
      if (!file.type.match('image.*')) {
        setError('Please select an image file (JPEG, PNG).');
        return;
      }
      
      setSelectedFile(file);
      
      try {
        // Create a preview URL
        const fileUrl = URL.createObjectURL(file);
        setPreviewUrl(fileUrl);
        console.log("File selected:", file.name, file.type, file.size);
      } catch (error) {
        console.error("Error creating preview:", error);
        setError("Failed to preview the file");
      }
    }
  };
  
  // Parse text to extract receipt data
  const parseReceiptText = (text) => {
    console.log("OCR Result:", text);
    
    // Initialize extracted data
    const extractedData = {
      amount: '',
      merchantName: '',
      date: new Date().toISOString().slice(0, 10)
    };
    
    // Extract total amount
    // Look for common patterns like "Total", "Amount", "Sum", etc. followed by numbers
    const totalRegex = /(?:total|amount|sum|grand total|bill total|to pay|balance|net amount)(?:[^\d$â‚¬Â£â‚¹]*)([\d,.]+)/i;
    const amountMatch = text.match(totalRegex);
    
    if (amountMatch && amountMatch[1]) {
      // Clean up the amount (remove commas, spaces, etc.)
      const cleanAmount = amountMatch[1].replace(/[^\d.]/g, '');
      extractedData.amount = cleanAmount;
    }
    
    // Extract date
    // Look for common date formats
    const dateRegex = /(\d{1,2})[\/\-\.](\d{1,2})[\/\-\.](\d{2,4})/g;
    const dateMatches = [...text.matchAll(dateRegex)];
    
    if (dateMatches.length > 0) {
      // Take the first match as the date
      const dateMatch = dateMatches[0];
      let day = dateMatch[1];
      let month = dateMatch[2];
      let year = dateMatch[3];
      
      // Add century if year is only 2 digits
      if (year.length === 2) {
        year = '20' + year;
      }
      
      // Format as YYYY-MM-DD for the input
      extractedData.date = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
    }
    
    // Extract merchant name
    // Usually one of the first lines or near words like "Restaurant", "Store", etc.
    const lines = text.split('\n').filter(line => line.trim() !== '');
    
    if (lines.length > 0) {
      // Try to find a line that might be a business name
      // Often one of the first 5 lines that's not a date or address
      const potentialNames = lines.slice(0, 5).filter(line => {
        // Filter out lines that are likely not names
        return !line.match(/^\d/) && // Doesn't start with a digit
               !line.match(/^tel|phone|fax|date|time/i) && // Not contact info
               line.length > 3 && // Not too short
               line.length < 50; // Not too long
      });
      
      if (potentialNames.length > 0) {
        extractedData.merchantName = potentialNames[0].trim();
      }
    }
    
    return extractedData;
  };
  
  const handleScan = async () => {
    if (!selectedFile) {
      setError('Please select an image first');
      return;
    }
    
    setIsLoading(true);
    setError('');
    setOcrProgress(0);
    
    try {
      // First update the context with basic image info
      console.log('Starting receipt scan process with file:', selectedFile.name);
      await scanReceipt(selectedFile);
      
      // Now use Tesseract.js to perform OCR on the image
      const worker = await createWorker({
        logger: progress => {
          console.log('OCR Progress:', progress);
          // Update progress percentage (0-100)
          if (progress.status === 'recognizing text') {
            setOcrProgress(Math.round(progress.progress * 100));
          }
        }
      });
      
      // Choose the appropriate language for your receipts
      // 'eng' for English, you can add more languages if needed
      await worker.loadLanguage('eng');
      await worker.initialize('eng');
      
      // Perform OCR on the image
      const { data: { text } } = await worker.recognize(selectedFile);
      
      // Extract receipt data from the OCR text
      const extractedData = parseReceiptText(text);
      
      console.log('Extracted data:', extractedData);
      
      // Update local receipt data with extracted values
      setReceiptData(prev => ({
        ...prev,
        amount: extractedData.amount || prev.amount,
        date: extractedData.date || prev.date,
        description: extractedData.merchantName || prev.description,
        notes: `Extracted text:\n${text.substring(0, 300)}${text.length > 300 ? '...' : ''}`
      }));
      
      // Terminate the worker to free up resources
      await worker.terminate();
      
    } catch (error) {
      console.error('Error in OCR process:', error);
      setError(error.message || 'An error occurred while scanning');
    } finally {
      setIsLoading(false);
      setOcrProgress(0);
    }
  };
  
  const handleAddExpense = async () => {
    if (!receiptScanStatus.data) {
      setError('No receipt data available');
      return;
    }
    
    setIsLoading(true);
    
    try {
      // Combine receiptScanStatus.data with local form data
      const dataToSubmit = {
        ...receiptScanStatus.data,
        amount: receiptData.amount,
        date: receiptData.date,
        description: receiptData.description,
        category: receiptData.category,
        notes: receiptData.notes,
        fromReceipt: true,
        receiptImageUrl: previewUrl
      };
      
      console.log('Adding expense from receipt data:', dataToSubmit);
      const result = await addExpenseFromReceipt(dataToSubmit);
      
      if (result) {
        // Successfully added expense, navigate to dashboard
        navigate('/');
      } else {
        setError('Failed to add expense. Please try again.');
      }
    } catch (error) {
      console.error('Error adding expense from receipt:', error);
      setError(error.message || 'Failed to add expense');
    } finally {
      setIsLoading(false);
    }
  };
  
  const handleCancel = () => {
    setSelectedFile(null);
    setPreviewUrl(null);
    setError('');
    // Return to previous page
    navigate(-1);
  };
  
  // Get safe categories array
  const safeCategories = Array.isArray(expenseCategories) ? expenseCategories : ['Food', 'Shopping', 'Transportation', 'Other'];
  
  return (
    <div className="receipt-scanner">
      <h1>Scan Receipt</h1>
      
      <div className="scanner-container">
        {!receiptScanStatus.data ? (
          <>
            <div className="upload-area">
              {previewUrl ? (
                <img 
                  src={previewUrl} 
                  alt="Receipt preview" 
                  className="receipt-preview" 
                />
              ) : (
                <div className="upload-placeholder">
                  <i className="upload-icon">ðŸ“·</i>
                  <p>Upload receipt image</p>
                </div>
              )}
              
              <input
                type="file"
                accept="image/*"
                onChange={handleFileChange}
                className="file-input"
                id="receipt-upload"
              />
              <label htmlFor="receipt-upload" className="upload-button">
                Choose Image
              </label>
            </div>
            
            {/* Display both error types */}
            {error && (
              <div className="scan-error">
                {error}
              </div>
            )}
            
            {receiptScanStatus.error && !error && (
              <div className="scan-error">
                {receiptScanStatus.error}
              </div>
            )}
            
            {/* Show OCR progress when scanning */}
            {isLoading && ocrProgress > 0 && (
              <div className="ocr-progress">
                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ width: `${ocrProgress}%` }}
                  ></div>
                </div>
                <div className="progress-text">
                  Extracting text: {ocrProgress}%
                </div>
              </div>
            )}
            
            <div className="scan-actions">
              <button 
                className="scan-button"
                onClick={handleScan}
                disabled={!selectedFile || isLoading}
              >
                {isLoading ? 'Scanning...' : 'Scan Receipt'}
              </button>
              <button 
                className="cancel-button" 
                onClick={handleCancel}
                disabled={isLoading}
              >
                Cancel
              </button>
            </div>
          </>
        ) : (
          <div className="scan-results">
            <h2>Receipt Details</h2>
            
            {previewUrl && (
              <div style={{marginBottom: '20px', textAlign: 'center'}}>
                <img 
                  src={previewUrl} 
                  alt="Receipt preview" 
                  style={{maxWidth: '100%', maxHeight: '300px', border: '1px solid #ddd'}} 
                />
              </div>
            )}
            
            <div className="receipt-info">
              <div className="info-row">
                <label>Amount:</label>
                <input 
                  type="number" 
                  step="0.01"
                  value={receiptData.amount} 
                  onChange={(e) => setReceiptData({...receiptData, amount: e.target.value})}
                  placeholder="Enter receipt total"
                  style={{width: '70%', padding: '8px'}}
                />
              </div>
              
              <div className="info-row">
                <label>Date:</label>
                <input 
                  type="date"
                  value={receiptData.date} 
                  onChange={(e) => setReceiptData({...receiptData, date: e.target.value})}
                  style={{width: '70%', padding: '8px'}}
                />
              </div>
              
              <div className="info-row">
                <label>Merchant:</label>
                <input 
                  type="text"
                  value={receiptData.description} 
                  onChange={(e) => setReceiptData({...receiptData, description: e.target.value})}
                  placeholder="Enter merchant name"
                  style={{width: '70%', padding: '8px'}}
                />
              </div>
              
              <div className="info-row">
                <label>Category:</label>
                <select
                  value={receiptData.category}
                  onChange={(e) => setReceiptData({...receiptData, category: e.target.value})}
                  style={{width: '70%', padding: '8px'}}
                >
                  {safeCategories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
              </div>
              
              <div className="info-row">
                <label>Notes:</label>
                <textarea
                  value={receiptData.notes}
                  onChange={(e) => setReceiptData({...receiptData, notes: e.target.value})}
                  placeholder="Enter any additional notes"
                  style={{width: '70%', padding: '8px', minHeight: '60px'}}
                />
              </div>
            </div>
            
            {error && (
              <div className="scan-error">
                {error}
              </div>
            )}
            
            <div className="result-actions">
              <button 
                className="add-expense-btn" 
                onClick={handleAddExpense}
                disabled={isLoading || !receiptData.amount || !receiptData.description}
              >
                {isLoading ? 'Adding...' : 'Add Expense'}
              </button>
              <button 
                className="cancel-button" 
                onClick={handleCancel}
                disabled={isLoading}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReceiptScanner;
