// src/services/EmailService.js
import emailjs from 'emailjs-com';

// Initialize EmailJS with your user ID
// You need to sign up for EmailJS and get these values
const EMAILJS_USER_ID = 'g0Zv3mEDUVljCuJyL'; // Replace with your actual EmailJS user ID
const EMAILJS_SERVICE_ID = 'service_uk2jyu5'; // Replace with your EmailJS service ID
const EMAILJS_TEMPLATE_ID = 'template_kjrzsq6'; // Replace with your template ID

// Initialize EmailJS
emailjs.init(EMAILJS_USER_ID);

// Function to send budget alert email
export const sendBudgetAlertEmail = async (user, category, budget, spent) => {
  // Verify that we have a user with an email address
  if (!user || !user.email) {
    console.error('Cannot send email: User email is missing', user);
    return {
      success: false,
      message: 'User email is missing'
    };
  }
  
  console.log('Sending budget alert email to:', user.email);
  
  const to = user.email;
  const over_amount = (spent - budget).toFixed(2);
  
  // Prepare template parameters
  const templateParams = {
    to_email: to,
    to_name: user.name || to.split('@')[0], // Use name or part before @ as name
    category: category,
    budget: budget.toFixed(2),
    spent: spent.toFixed(2),
    over_amount: over_amount
  };
  
  console.log('Email template parameters:', templateParams);
  
  // Send email using EmailJS
  try {
    const response = await emailjs.send(
      EMAILJS_SERVICE_ID,
      EMAILJS_TEMPLATE_ID,
      templateParams
    );
    
    console.log('Email sent successfully:', response);
    
    return {
      success: true,
      message: 'Email sent successfully'
    };
  } catch (error) {
    console.error('Error sending email:', error);
    return {
      success: false,
      message: 'Failed to send email',
      error: error.message
    };
  }
};
