import React, { useContext, useState, useEffect } from 'react';
import { ExpenseContext } from '../context/ExpenseContext';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell } from 'recharts';
import BudgetAlerts from './BudgetAlerts';
import './Dashboard.css';

const Dashboard = () => {
  const { expenses, budgets, alerts, incomes } = useContext(ExpenseContext);
  const [chartData, setChartData] = useState([]);
  const [categoryData, setCategoryData] = useState([]);
  const [timeframe, setTimeframe] = useState('month');
  
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#A28AD3', '#FF6B6B', '#4ECDC4'];
  
  useEffect(() => {
    prepareChartData();
    prepareCategoryData();
  }, [expenses, timeframe]);
  
  const currentMonthData = () => {
    const now = new Date();
    const currentMonth = now.getMonth();
    const currentYear = now.getFullYear();
    
    const monthlyExpenses = expenses.filter(expense => {
      const expenseDate = new Date(expense.date);
      return expenseDate.getMonth() === currentMonth && 
             expenseDate.getFullYear() === currentYear;
    });
    
    const monthlyIncome = incomes ? incomes.filter(income => {
      const incomeDate = new Date(income.date);
      return incomeDate.getMonth() === currentMonth && 
             incomeDate.getFullYear() === currentYear;
    }) : [];
    
    const totalExpenses = monthlyExpenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);
    const totalIncome = monthlyIncome.reduce((sum, income) => sum + parseFloat(income.amount), 0);
    
    return {
      totalExpenses,
      totalIncome,
      balance: totalIncome - totalExpenses
    };
  };
  
  const prepareChartData = () => {
    if (expenses.length === 0) return;
    
    const now = new Date();
    let filteredExpenses = [];
    
    if (timeframe === 'week') {
      // Last 7 days
      const lastWeek = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
      filteredExpenses = expenses.filter(expense => new Date(expense.date) >= lastWeek);
    } else if (timeframe === 'month') {
      // Current month
      filteredExpenses = expenses.filter(expense => {
        const expenseDate = new Date(expense.date);
        return expenseDate.getMonth() === now.getMonth() && 
               expenseDate.getFullYear() === now.getFullYear();
      });
    } else if (timeframe === 'year') {
      // Current year
      filteredExpenses = expenses.filter(expense => {
        const expenseDate = new Date(expense.date);
        return expenseDate.getFullYear() === now.getFullYear();
      });
    }
    
    // Group by date
    const groupedData = {};
    
    filteredExpenses.forEach(expense => {
      const date = expense.date;
      if (!groupedData[date]) {
        groupedData[date] = 0;
      }
      groupedData[date] += parseFloat(expense.amount);
    });
    
    // Convert to array for recharts
    const chartDataArray = Object.keys(groupedData)
      .sort()
      .map(date => ({
        date,
        amount: groupedData[date]
      }));
      
    setChartData(chartDataArray);
  };
  
  const prepareCategoryData = () => {
    if (expenses.length === 0) return;
    
    const now = new Date();
    let filteredExpenses = [];
    
    if (timeframe === 'week') {
      const lastWeek = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
      filteredExpenses = expenses.filter(expense => new Date(expense.date) >= lastWeek);
    } else if (timeframe === 'month') {
      filteredExpenses = expenses.filter(expense => {
        const expenseDate = new Date(expense.date);
        return expenseDate.getMonth() === now.getMonth() && 
               expenseDate.getFullYear() === now.getFullYear();
      });
    } else if (timeframe === 'year') {
      filteredExpenses = expenses.filter(expense => {
        const expenseDate = new Date(expense.date);
        return expenseDate.getFullYear() === now.getFullYear();
      });
    }
    
    // Group by category
    const categoryTotals = {};
    
    filteredExpenses.forEach(expense => {
      const { category, amount } = expense;
      if (!categoryTotals[category]) {
        categoryTotals[category] = 0;
      }
      categoryTotals[category] += parseFloat(amount);
    });
    
    // Convert to array for recharts
    const categoryDataArray = Object.keys(categoryTotals).map(category => ({
      name: category,
      value: categoryTotals[category]
    }));
    
    setCategoryData(categoryDataArray);
  };
  
  // Calculate monthly data outside the JSX
  const monthlyData = currentMonthData();
  const totalExpenses = monthlyData.totalExpenses;
  const totalIncome = monthlyData.totalIncome;
  const balance = monthlyData.balance;
  
  return (
    <div className="dashboard">
      <h1>Expense Dashboard</h1>
      
      {alerts.filter(alert => !alert.read).length > 0 && (
        <BudgetAlerts />
      )}
      
      <div className="summary-boxes">
        <div className="summary-box income-box">
          <div className="summary-title">Monthly Income</div>
          <div className="summary-value">${totalIncome.toFixed(2)}</div>
        </div>
        
        <div className="summary-box expense-box">
          <div className="summary-title">Monthly Expenses</div>
          <div className="summary-value">${totalExpenses.toFixed(2)}</div>
        </div>
        
        <div className={`summary-box balance-box ${balance >= 0 ? 'positive' : 'negative'}`}>
          <div className="summary-title">Balance</div>
          <div className="summary-value">${Math.abs(balance).toFixed(2)}</div>
          <div className="summary-label">{balance >= 0 ? 'Surplus' : 'Deficit'}</div>
        </div>
      </div>
      
      <div className="timeframe-selector">
        <button 
          className={timeframe === 'week' ? 'active' : ''} 
          onClick={() => setTimeframe('week')}>
          Week
        </button>
        <button 
          className={timeframe === 'month' ? 'active' : ''} 
          onClick={() => setTimeframe('month')}>
          Month
        </button>
        <button 
          className={timeframe === 'year' ? 'active' : ''} 
          onClick={() => setTimeframe('year')}>
          Year
        </button>
      </div>
      
      <div className="chart-container">
        <h2>Spending Over Time</h2>
        {chartData.length > 0 ? (
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip formatter={(value) => [`$${value}`, 'Amount']} />
              <Legend />
              <Line type="monotone" dataKey="amount" stroke="#8884d8" activeDot={{ r: 8 }} />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <p>No data available for the selected timeframe</p>
        )}
      </div>
      
      <div className="charts-row">
        <div className="chart-container">
          <h2>Spending by Category</h2>
          {categoryData.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={categoryData}
                  cx="50%"
                  cy="50%"
                  labelLine={true}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                >
                  {categoryData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']} />
              </PieChart>
            </ResponsiveContainer>
          ) : (
            <p>No data available for the selected timeframe</p>
          )}
        </div>
        
        <div className="chart-container">
          <h2>Budget vs. Actual</h2>
          {categoryData.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <BarChart
                data={categoryData.map(item => ({
                  name: item.name,
                  actual: item.value,
                  budget: budgets[item.name] || 0
                }))}
              >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']} />
                <Legend />
                <Bar dataKey="actual" fill="#FF8042" name="Actual" />
                <Bar dataKey="budget" fill="#8884d8" name="Budget" />
              </BarChart>
            </ResponsiveContainer>
          ) : (
            <p>No data available for the selected timeframe</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
